$Mes = date("m");
$Ano = date("Y");

if(isset($_REQUEST["mesano"])){
    $Busca = addslashes(filter_input(INPUT_GET, 'mesano'));
    $Proc = explode("/", $Busca);
    $Mes = $Proc[0];
    if(strLen($Mes) < 2){
        $Mes = "0".$Mes;
    }
    $Ano = $Proc[1];
    $Data = date('01/'.$Mes.'/'.$Ano);
}else{
    $rs = pg_query($Conec, "SELECT MIN(dataescala) FROM ".$xProj.".escala_adm WHERE ativo = 1 ");
    $tbl = pg_fetch_row($rs);
    $MaxData = $tbl[0];
    $Proc = explode("-", $MaxData);
    $Ano = $Proc[0];
    $Mes = $Proc[1];
    $Mes = ($Mes - 1);
    $Data = date('01/'.$Mes.'/'.$Ano);
}
echo "Mês: ".$Mes.'/'.$Ano;
